ADD COLUMN IF NOT EXISTS code VARCHAR(20),
ADD COLUMN IF NOT EXISTS name VARCHAR(255),
ADD COLUMN IF NOT EXISTS description TEXT,
ADD COLUMN IF NOT EXISTS type VARCHAR(50),
ADD COLUMN IF NOT EXISTS status VARCHAR(20) DEFAULT 'ativo',
ADD COLUMN IF NOT EXISTS createdBy INT,
ADD COLUMN IF NOT EXISTS updatedBy INT;
ALTER TABLE cost_centers MODIFY COLUMN createdAt DATETIME DEFAULT CURRENT_TIMESTAMP;
ALTER TABLE cost_centers MODIFY COLUMN updatedAt DATETIME DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP;

